import * as React from 'react';
import styled, { themed, css } from '../../utils/styled';
import { InteractiveListWrapperProps, InteractiveListDirection, InteractiveListBorderType, InteractiveListItem, InteractiveListProps, InteractiveListState, } from './InteractiveList.types.part';
import { Icon } from '../Icon';
import { Checkbox } from '../Checkbox';
import { KeyCodes } from '../../utils/keyCodes';
import { pacificBlue, transparent } from '../../colors';
import { distance } from '../../distance';
import { getFontStyle } from '../../textStyles';
interface ListItemProps {
    selected: boolean;
    hovered: boolean;
    disabled?: boolean;
    onClick?(e: React.MouseEvent): void;
    onMouseMove?(e: React.MouseEvent): void;
}
interface ListItemContentProps {
    condensed: boolean;
    showTick: boolean;
}
interface ListItemIconProps {
    condensed: boolean;
}
const InteractiveListContainer = styled.div `
  position: relative;
  outline: none;
`;
const ListWrapper = styled('ul')<InteractiveListWrapperProps>(themed(({ open, border, direction, theme: { ui4 } }) => css `
      display: ${open ? 'block' : 'none'};
      list-style: none;
      width: 100%;
      position: relative;
      transform: translateY(${direction === InteractiveListDirection.normal ? 0 : -100}%);
      box-sizing: border-box;
      box-shadow: none;
      margin: 0;
      padding: 0;
      background: ${transparent};
      border: 1px solid ${border === InteractiveListBorderType.none ? transparent : ui4};
      ${direction === InteractiveListDirection.normal ? 'border-top: none' : 'border-bottom: none'};
      max-height: 100%;
      overflow-y: auto;
      z-index: 100;
    `));
const ListItem = styled.li<ListItemProps>(themed(({ hovered, theme: { ui3, text2 }, disabled }) => css `
      ${getFontStyle({ size: 'medium' })}

      background: ${hovered ? ui3 : transparent};
      color: ${text2};
      list-style: none;
      box-sizing: border-box;
      opacity: ${disabled ? '0.5' : '1.0'};
      cursor: ${disabled ? 'not-allowed' : 'pointer'};
      display: block;
      width: 100%;
      height: auto;
      position: relative;

      a {
        color: inherit;
        display: block;
        text-decoration: none;

        &:hover {
          text-decoration: none;
        }
      }
    `));
const ListItemInnerContainer = styled.div `
  display: flex;
  align-items: center;
`;
const ListItemContent = styled.div `
  width: 100%;
`;
const ListItemContentPadding = styled('div')<ListItemContentProps> `
  padding: ${props => (props.condensed ? `${distance.small} ${distance.medium}` : distance.medium)};
  ${props => (props.showTick ? 'padding-right: 0;' : '')};
`;
const ListItemContentComponentPadding = styled('div')<ListItemContentProps> `
  > * {
    padding: ${props => (props.condensed ? `${distance.small} ${distance.medium}` : distance.medium)};
    ${props => (props.showTick ? 'padding-right: 0;' : '')};
