import * as React from 'react';
import styled, { themed, css } from '../../utils/styled';
import { StandardProps } from '../../common';
import { distance } from '../../distance';
import { cyan } from '../../colors';
export interface MenuListItemProps extends StandardProps {
    /**
     * Whether the button is active.
     */
    active?: boolean;
    /**
     * The content of the component.
     */
    children?: React.ReactNode;
    /**
     * Making a row more condensed.
     */
    condensed?: boolean;
}
const ActiveStyle = css `
  &:before {
    position: absolute;
    content: '';
    width: 4px;
    height: 100%;
    background: ${themed(({ theme }) => theme.ui0)};
    left: 0;
  }
`;
const StyledMenuListItem = styled('li')<MenuListItemProps> `
  display: flex;
  position: relative;
  align-items: center;
  color: ${themed(props => props.theme.text6)};
  text-decoration: none;
  height: ${({ condensed }) => (condensed ? distance.xlarge : distance.xxlarge)};
  padding: 0 0 0 ${distance.large};
  ${({ active }) => (active ? ActiveStyle : '')};
`;
export const MenuListItem = Object.assign(((({ active = false, condensed = false, ...props }) => (<StyledMenuListItem {...props} condensed={condensed} active={active}/>)) as React.SFC<MenuListItemProps>), { inner: {
        get StyledMenuListItem() { return StyledMenuListItem as typeof StyledMenuListItem; }
    } });
MenuListItem.displayName = 'MenuListItem';
