import React from 'react';
import './preload.css';
import LoadingProgress from 'react-js-loading-progress-bar';

import cardOnetest from '../../assets/img/uguru/ttt-01.png';
import cardTwotest from '../../assets/img/san.png';
import cardThreetest from '../../assets/img/apexvr-01.png';
import cardFourtest from '../../assets/img/ddd-01.png';
import cardFivetest from '../../assets/img/dasdasdgg-01.png';
import vidtest from '../../assets/vids/students_working.mp4';

const cardOne = require("../../assets/img/uguru/ttt-01.png");

const images = [
    { id: 1, path: "../../assets/img/uguru/ttt-01.png" },
    { id: 2, path: "./assets/img/san.png" },
    { id: 3, path: "./assets/img/apexvr-01.png" },
  ];


var TotalNumber = 0;
var CurrentNumber = 0;



var onloadCallback = function(){
    // Increment the counter
    CurrentNumber++;

    // Verify if the counter is less than the number of images
    if(CurrentNumber < TotalNumber){
        return;
    }

};    

cardOne.onload = onloadCallback;
cardTwo.onload = onloadCallback;


export const PreLoad = () => (
    <>







     <LoadingProgress
       active={true}
       total={0}
       current={1}
       showCompact       

/>

        <div id="preload">
            <img src={require( "" + images.path )} />
            <img src={cardTwo} />
            <img src={cardThree} />
            <img src={cardFour} />
            <img src={cardFive} />
            <img src={vid} />
        </div>

    </>
    )

    export default PreLoad;
