        const { message = 'Drop files here to upload', multiple, info, icon = 'FileDownload', theme, value: _0, defaultValue: _1, disabled, onChange: _2, preview: _3, onInput: _5, children, ...props } = this.props;
        return (<div {...props}>
        <StyledDropzone active={over} disabled={disabled} onDrop={this.onDrop} onDragOver={this.onDragOver} onDragLeave={this.onDragLeave} onDragEnter={this.onDragEnter} onClick={this.openFilePicker}>
          <DropzoneLabel>
            <Icon name={icon} size={2}/>
            <StyledLabel>
              {message}
              <FileInput disabled={disabled} ref={this.setInputRef} type="file" multiple={multiple} value="" onChange={this.onClick}/>
              <StyledAction active={over} disabled={disabled}>
                <IconLink icon="Add">{children}</IconLink>
              </StyledAction>
            </StyledLabel>
          </DropzoneLabel>
        </StyledDropzone>
        {value && value.length > 0 && (<StyledFileList>
            {value.map(file => (this.props.preview ? this.renderPreview(file) : this.renderItem(file)))}
          </StyledFileList>)}
        {showInputInfo(error, info)}
      </div>);
    }
    static inner = {
        get StyledFileItem() { return StyledFileItem as typeof StyledFileItem; },
        get Remove() { return Remove as typeof Remove; },
        get Icon() { return Icon as typeof Icon; },
        get StyledFileImagePreview() { return StyledFileImagePreview as typeof StyledFileImagePreview; },
        get Spinner() { return Spinner as typeof Spinner; },
        get StyledDropzone() { return StyledDropzone as typeof StyledDropzone; },
        get DropzoneLabel() { return DropzoneLabel as typeof DropzoneLabel; },
        get StyledLabel() { return StyledLabel as typeof StyledLabel; },
        get FileInput() { return FileInput as typeof FileInput; },
        get StyledAction() { return StyledAction as typeof StyledAction; },
        get IconLink() { return IconLink as typeof IconLink; },
        get StyledFileList() { return StyledFileList as typeof StyledFileList; }
