import * as React from 'react';
import styled, { themed } from '../../utils/styled';
import { InteractiveList, InteractiveListChangeEvent, InteractiveListWrapperProps, InteractiveListItemObject, } from '../InteractiveList';
import { cyan, transparent } from '../../colors';
import { Icon } from '../Icon';
import { getFontStyle } from '../../textStyles';
import onClickOutside from 'react-onclickoutside';
interface StyledWrapperProps {
    open: boolean;
}
const StyledWrapper = styled.div<StyledWrapperProps> `
  box-sizing: border-box;
  box-shadow: none;
  margin: 0 0 ${props => (props.open ? '10px' : '0')} 0;
  padding: 0;
  border: 1px solid ${themed(({ theme, open }) => (open ? theme.ui0 : transparent))};
`;
const MenuCaret = styled.div `
  position: absolute;
  top: 8px;
  right: 9px;
  fill: rgba(0, 139, 208, 1);
`;
const MenuTitle = styled.span `
  ${getFontStyle({ size: 'large', weight: 'regular' })}

  border-bottom: 2px solid black;
`;
const SelectedMenu = styled.div `
  position: relative;
  padding: 10px;
  cursor: pointer;
`;
export type TabHeaderItem = InteractiveListItemObject;
export interface SlideDownTabsProps {
    /**
     * The tab items.
     */
    data: Array<TabHeaderItem>;
    /**
     * Selected tab index.
     */
    selectedIndex: number;
    /**
     * Event triggered once the item selected.
     */
    onChange(index: number): void;
}
export interface SlideDownTabsState {
    isOpen: boolean;
}
const CustomWrapper: React.SFC<InteractiveListWrapperProps> = ({ border: _0, direction: _1, open: _2, ...props }) => (<div {...props}/>);
class SlideDownTabsInt extends React.Component<SlideDownTabsProps, SlideDownTabsState> {
    constructor(props: SlideDownTabsProps) {
        super(props);
        this.state = {
            isOpen: false,
        };
    }
    private hide = () => {
        this.setState({ isOpen: false });
    };
    private toggle = (e: React.MouseEvent<HTMLDivElement>) => {
        this.setState({ isOpen: !this.state.isOpen });
        e.preventDefault();
    };
    private onChange = ({ value }: InteractiveListChangeEvent) => {
        this.props.onChange(value[0]);
        this.setState({
            isOpen: false,
        });
    };
    handleClickOutside = this.hide;
    render() {
        const { isOpen } = this.state;
        const { data, selectedIndex } = this.props;
        const interactiveList = data.map((item, index) => (index === selectedIndex ? undefined : item));
        return (<StyledWrapper open={isOpen}>
        <SelectedMenu onMouseDown={this.toggle}>
          <MenuTitle>{data[selectedIndex].content}</MenuTitle>
          <MenuCaret>
            <Icon name={isOpen ? 'KeyboardArrowUp' : 'KeyboardArrowDown'} size="24px" color={cyan}/>
          </MenuCaret>
        </SelectedMenu>
        <InteractiveList data={interactiveList} open={isOpen} onBlur={this.hide} autoFocus onChange={this.onChange} customWrapper={CustomWrapper} onClickOutside={() => { }}/>
      </StyledWrapper>);
    }
    static inner = {
        get StyledWrapper() { return StyledWrapper as typeof StyledWrapper; },
        get SelectedMenu() { return SelectedMenu as typeof SelectedMenu; },
        get MenuTitle() { return MenuTitle as typeof MenuTitle; },
        get MenuCaret() { return MenuCaret as typeof MenuCaret; },
        get Icon() { return Icon as typeof Icon; },
        get InteractiveList() { return InteractiveList as typeof InteractiveList; }
    };
}
export const SlideDownTabs = onClickOutside(SlideDownTabsInt);
export const DropdownContainer = styled.div ``;
export const TabContent = styled.div ``;
export interface TabItemProps {
    active?: boolean;
}
export const TabItem = styled.div<TabItemProps> `
  ${props => (props.active ? '' : 'display: none;')};
`;
