import React from 'react';
import './preload.css';
import LoadingProgress from 'react-js-loading-progress-bar';

const cardOne = require("../../assets/img/uguru/ttt-01.png",);
const cardTwo = require("../../assets/img/san.png");
const cardThree = require("../../assets/img/apexvr-01.png");
const cardFour = require("../../assets/img/ddd-01.png");
const cardFive = require("../../assets/img/dasdasdgg-01.png");
const vid = require("../../assets/vids/students_working.mp4");




var TotalNumber = 0;
var CurrentNumber = 0;

var onloadCallback = function(){
    // Increment the counter
    Counter++;

    // Verify if the counter is less than the number of images
    if(Counter < TotalImages){
        return;
    }

    // Trigger the final callback if is the last img
    allLoadedCallback();
};    

// The callback that is executed when all the images have been loaded or not
var allLoadedCallback = function(){
    console.log("Atention, the following images were not loaded ");
};

cardOne.onload = onloadCallback;
cardOne.cardTwo = onloadCallback;


export const PreLoad = () => (
    <>
     <LoadingProgress
       active={true}
       total={this.state.total}
       current={this.state.current}
       showCompact       

/>

        <div id="preload">
            <img src={cardOne} />
            <img src={cardTwo} />
            <img src={cardThree} />
            <img src={cardFour} />
            <img src={cardFive} />
            <img src={vid} />
        </div>

    </>
    )

    export default PreLoad;
