import React from 'react';
import Route from 'react-router-dom';
import { spring, AnimatedSwitch } from 'react-router-transition';

import { Home } from '../home/home'
import { Contact } from '../contact/contact'
import { Uguru } from '../uguru/uguru'
import { Dasdasdigital } from '../dasdasdigital/dasdasdigital'
import { Sanchezcoffeeco } from '../sanchezcoffeeco/sanchezcoffeeco'
import { Apexvr } from '../apexvr/apexvr'
import { Stepsaver } from '../stepsaver/stepsaver'

import './test.css'


// we need to map the `scale` prop we define below
// to the transform style property
function mapStyles(styles) {
  return {
    opacity: styles.opacity,
    transform: `scale(${styles.scale})`,
  };
}

// wrap the `spring` helper to use a bouncy config
function bounce(val) {
  return spring(val, {
    stiffness: 330,
    damping: 22,
  });
}

// child matches will...
const bounceTransition = {
  // start in a transparent, upscaled state
  atEnter: {
    opacity: 0,
    scale: 1.2,
  },
  // leave in a transparent, downscaled state
  atLeave: {
    opacity: bounce(0),
    scale: bounce(0.8),
  },
  // and rest at an opaque, normally-scaled state
  atActive: {
    opacity: bounce(1),
    scale: bounce(1),
  },
};

export default () => (
  <AnimatedSwitch
    atEnter={bounceTransition.atEnter}
    atLeave={bounceTransition.atLeave}
    atActive={bounceTransition.atActive}
    mapStyles={mapStyles}
    className="route-wrapper"
  >
    <Route path="/uguru" component={Uguru}></Route>
    <Route path="/sanchezcoffeeco" component={Sanchezcoffeeco}></Route>
    <Route path="/apexvr" component={Apexvr}></Route>
    <Route path="/dasdasdigital" component={Dasdasdigital}></Route>
    <Route path="/stepsaver" component={Stepsaver}></Route>
    <Route path="/contact" component={Contact}></Route>
    <Route path="/" component={Home}> </Route>
  </AnimatedSwitch>
);
