import * as React from 'react';
import styled from '../../utils/styled';
import { dark } from '../../colors';
import { getFontStyle } from '../../textStyles';
const RootContainer = styled.div `
  ${getFontStyle({ size: 'small' })}

  color: ${dark};
`;
export interface PaginationProps {
    host?: string | React.ComponentClass | React.StatelessComponent;
    controls: React.ReactChild;
    content: React.ReactNode;
}
export const PaginationLayout = Object.assign(((({ host = 'div', controls, content, ...props }) => {
    const Content = host;
    return (<RootContainer>
      <Content {...props}>{content}</Content>
      {controls}
    </RootContainer>);
}) as React.SFC<PaginationProps>), { inner: {
        get RootContainer() { return RootContainer as typeof RootContainer; }
    } });
